require "openssl"

def sqrt(a)
  begv = 1
  endv = a
  while endv > begv + 1
     mid = (endv + begv)/2
     if mid ** 2 <= a
        begv = mid
     else
        endv = mid
     end
  end
  return begv 
end

N = 
CIPHERTEXT =  

a = sqrt(N)+1
x = sqrt(a*a - N)

p = a - x
q = a + x

phi = (p-1)*(q-1)
e = 65537 
d = e.to_bn.mod_inverse(phi)

plaintext = CIPHERTEXT.to_bn.mod_exp(d, N)
plaintext = plaintext.to_s(16)
plaintext = [plaintext].pack("H*").split("\x00")[1]

puts "Plaintext: #{plaintext}"
